mapper.update:
UPDATE bmall.order
SET state = #{state}
where id = #{id}

mapper.select:
SELECT commodity_id
FROM bmall.order_commodity
WHERE order_id = #{id}

SELECT c.id
FROM bmall.commodity c INNER JOIN bmall.order_commodity oc ON c.id = oc.commodity_id
WHERE oc.order_id = #{id}

select IFNULL (sum(price),0)
from `order` where `issueTime`
between #{dateBegin,jdbcType=TIMESTAMP} and #{dateEnd,jdbcType=TIMESTAMP}

select IFNULL (count(*),0)
from `order` where `issueTime`
between #{dateBegin,jdbcType=TIMESTAMP} and #{dateEnd,jdbcType=TIMESTAMP}
